{/* Visual Side - Zen/Growth Inspired Circular Version */}
<motion.div
  className="flex-1 relative max-w-md w-full"
  initial={{ opacity: 0, scale: 0.8 }}
  animate={{ opacity: 1, scale: 1 }}
  transition={{ delay: 0.5, duration: 1 }}
>
  <div className="relative flex justify-center items-center">

    {/* Rotating Animated Gradient Ring */}
    <motion.div
      animate={{ rotate: 360 }}
      transition={{ duration: 15, repeat: Infinity, ease: "linear" }}
      className="absolute w-[320px] h-[320px] md:w-[440px] md:h-[440px] rounded-full border-2 border-dashed border-orange-500/30"
    />

    {/* Secondary Glow Aura */}
    <div className="absolute w-[280px] h-[280px] md:w-[400px] md:h-[400px] rounded-full bg-orange-500/5 blur-3xl" />

    {/* Main Circular Image Container */}
    <div className="relative w-72 h-72 md:w-[380px] md:h-[380px] rounded-full overflow-hidden border-4 border-white/10 shadow-[0_0_50px_rgba(249,115,22,0.2)] z-10 group">
      <img
        src="/images/Mamtha.jpeg" 
        alt="Mamtha Gowda G S"
        className="w-full h-full object-cover transition-transform duration-1000 group-hover:scale-110"
      />
      {/* Subtle overlay to blend with dark theme */}
      <div className="absolute inset-0 bg-gradient-to-b from-transparent via-transparent to-black/20" />
    </div>

    {/* Floating Mindset Badge - Adjusted for Circle */}
    <motion.div
      className="absolute bottom-4 -right-2 bg-white/90 backdrop-blur-xl px-5 py-3 rounded-2xl border border-white/10 z-20 shadow-2xl text-black"
      initial={{ x: 20, opacity: 0 }}
      animate={{ x: 0, opacity: 1 }}
      transition={{ delay: 1.5 }}
    >
      <div className="flex items-center gap-3">
        <div className="p-2 bg-orange-100 rounded-lg">
          <Sun className="text-orange-600" size={18} />
        </div>
        <div>
          <div className="text-[9px] uppercase tracking-tighter text-gray-500 font-bold">Approach</div>
          <div className="text-xs font-black italic">Holistic & Actionable</div>
        </div>
      </div>
    </motion.div>

    {/* Heart Badge - Floating Animation */}
    <motion.div
      className="absolute top-4 left-0 p-3 bg-black/60 backdrop-blur-md rounded-full border border-white/10 z-20"
      animate={{ y: [0, -15, 0] }}
      transition={{ duration: 4, repeat: Infinity, ease: "easeInOut" }}
    >
      <Heart className="text-red-500 fill-red-500" size={18} />
    </motion.div>
  </div>
</motion.div>
