import React from 'react';
import { motion } from 'framer-motion';
import { Sun, Heart } from 'lucide-react'; // Ensure these are imported

const HeroVisual = () => {
  return (
    <motion.div
      className="flex-1 relative max-w-md w-full"
      initial={{ opacity: 0, scale: 0.8 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ delay: 0.5, duration: 1 }}
    >
      <div className="relative flex justify-center items-center">

        {/* 1. Rotating Animated Outer Ring */}
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
          className="absolute w-[300px] h-[300px] md:w-[420px] md:h-[420px] rounded-full border border-dashed border-orange-500/20"
        />

        {/* 2. Secondary Glow Aura (Softens the background) */}
        <div className="absolute w-[250px] h-[250px] md:w-[380px] md:h-[380px] rounded-full bg-orange-500/10 blur-[80px]" />

        {/* 3. Main Circular Image Container */}
        <div className="relative w-64 h-64 md:w-[350px] md:h-[350px] rounded-full overflow-hidden border-2 border-white/20 shadow-2xl z-10 group">
          <img
            src="/images/Mamtha.jpeg" 
            alt="Mamtha Gowda G S"
            className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-105"
          />
          {/* Subtle vignette for depth */}
          <div className="absolute inset-0 bg-gradient-to-b from-transparent to-black/30" />
        </div>

        {/* 4. Floating Mindset Badge (Glassmorphism) */}
        <motion.div
          className="absolute bottom-6 -right-4 bg-white/10 backdrop-blur-xl px-4 py-3 rounded-2xl border border-white/20 z-20 shadow-xl text-white"
          initial={{ x: 30, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ delay: 1.2 }}
        >
          <div className="flex items-center gap-3">
            <div className="p-2 bg-orange-500/20 rounded-lg">
              <Sun className="text-orange-400" size={18} />
            </div>
            <div className="flex flex-col">
              <span className="text-[10px] uppercase tracking-wider text-gray-400 font-semibold">Approach</span>
              <span className="text-sm font-bold italic">Holistic AI</span>
            </div>
          </div>
        </motion.div>

        {/* 5. Heart Badge - Floating Animation */}
        <motion.div
          className="absolute top-6 left-0 p-3 bg-black/40 backdrop-blur-md rounded-full border border-white/10 z-20"
          animate={{ y: [0, -12, 0] }}
          transition={{ duration: 3, repeat: Infinity, ease: "easeInOut" }}
        >
          <Heart className="text-red-500 fill-red-500" size={16} />
        </motion.div>
      </div>
    </motion.div>
  );
};

export default HeroVisual;
